var selectedDate = g_form.getValue('u_when_needed');
	selectedDate = new Date(selectedDate);
var currentDate = new Date();
	var flag = compareDates(currentDate, dateFormat,selectedDate,dateFormat);
	jslog('flag = '+ flag);
	//if(flag === -1) {
