		g_form.clearValue('u_when_needed');
		g_form.flash('u_when_needed', '#FF7F50', -2);
		g_form.showFieldMsg('u_when_needed','When needed date cannot be before todays date','error');
	}
   //Type appropriate comment here, and begin script below

